INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("DRPSRS@sdc.com",1, "Demand Responsive Pedestrian Safety Rumble Strips", "http://i.imgur.com/3TLT7QN.png", "The Demand Responsive Pedestrian Safety Rumble Strips (DRRS) is an innovative technology that increases pedestrian safety while reducing distracted driving.");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("HDSC@sdc.com",1, "Hoover Dam Security Checkpoint", "http://i.imgur.com/3TLT7QN.png", "The Demand Responsive Pedestrian Safety Rumble Strips (DRRS) is an innovative technology that increases pedestrian safety while reducing distracted driving.");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("LVBLR@sdc.com",1, "LVB Light Rail", "http://i.imgur.com/3TLT7QN.png", "This project aims to implement a high-speed transportation system in order to satisfy both the need for a connection through McCarran Airport, the Las Vegas Strip, and Downtown Las Vegas and relieve vehicle congestion.");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("UNLVPCF@sdc.com",1, "UNLV Paradise Campus Flooding", "http://i.imgur.com/3TLT7QN.png", "The team’s solution is to flatten the parking lot and incorporate porous pavement, along with adding solar panels to generate power and to shade the parking lot.");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("CASH@sdc.com",1, "Custom Autonomous Secure Home (CASH)", "http://i.imgur.com/3TLT7QN.png", "Our concept is to develop an application, which allows a user to integrate off-the-shelf home efficiency and security devices as these devices are purchased over time i.e. “plug and play”.");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("CLT@sdc.com",1, "Convention Logistics Tracker", "http://i.imgur.com/3TLT7QN.png", "The CLT app is developed with its focus on the attendee experience.");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("FLTR@sdc.com",1, "Filterable Stock News Scanner (FLTR)", "http://i.imgur.com/3TLT7QN.png", "We are here to provide a real-time on-demand platform that gives our users the sought-after news filter feature like the solutions currently available.");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("Linked@sdc.com",1, "Linked", "http://i.imgur.com/3TLT7QN.png", "We are here to provide a real-time on-demand platform that gives our users the sought-after news filter feature like the solutions currently available.");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("spots@sdc.com",1, "Spots", "http://i.imgur.com/3TLT7QN.png", "Spots will facilitate parking in parking lots with regular drivers by integrating all parking spaces on campus in a single system that will track parking spaces to notify and advise students and staff with important information about the parking spots on every lot.");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("APS@sdc.com",1, "Adaptive Power Supply", "http://i.imgur.com/3TLT7QN.png", "At a basic level, our solution is a charger that operates to the highest USB 3.0 standard to allow for the charging of multiple devices with varying power ratings and charging requirements.");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("CSPS@sdc.com",1, "Card Shoe Projection System", "http://i.imgur.com/3TLT7QN.png", "Our objective is to create a marketable product that can be used to assist casinos and businesses in hindering players from using advantage play, specifically edge-sorting.");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("CDP@sdc.com",1, "Casino Dice Project", "http://i.imgur.com/3TLT7QN.png", "Our solution with the advice of Dr. Sahl’s proposal was to have flashing LED dice that randomly settle on a specific color once rolled.");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("IS@sdc.com",1, "Impact Solutions", "http://i.imgur.com/3TLT7QN.png", "The purpose of our project is to detect the level of force when a football player is hit.");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("MCII@sdc.com",1, "Micro Grid II: Controls and Revisions", "http://i.imgur.com/3TLT7QN.png", "Conversion to a 24V DC system which includes a battery backup system for the controls allows maximum efficiency and versatility to the project.");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("TET@sdc.com",1, "The Electrical Tester", "http://i.imgur.com/3TLT7QN.png", "The Electrical Tester offers passive detection to alert the user immediately if they are working in the vicinity of a live circuit that could potentially cause electrical shock.");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("THB@sdc.com",1, "The Hi Ball", "http://i.imgur.com/3TLT7QN.png", "The Electrical Tester offers passive detection to alert the user immediately if they are working in the vicinity of a live circuit that could potentially cause electrical shock.");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("BMSAG@sdc.com",1, "Bombyx Mori Silk AntiMicrobial Gel", "http://i.imgur.com/3TLT7QN.png", "The team proposes a gel made of Bombyx Mori silk, or silkworm cocoon silk, mixed with an antimicrobial agent, like silver nanoparticles.");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("CQ@sdc.com",1, "Coanda Quadcopter", "http://i.imgur.com/3TLT7QN.png", "Our team’s solution to the flight duration problem involves adding an additional means of generating lift without significantly increasing the cost or weight of the UAV. ");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("DSTDT@sdc.com",1, "Damping System Test Drop Tower", "http://i.imgur.com/3TLT7QN.png", "Our solution involves a tower that is able to generate a wide range of shocks into a damping system with a large amount of control while allowing the system to be interchanged with others.");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("HSG@sdc.com",1, "Harco Spray Gun", "http://i.imgur.com/3TLT7QN.png", "The redesigned gun improves ergonomics while reducing weight by 1-1.5 lbs due to the use of plastic instead of aluminum.");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("Jox-t@sdc.com",1, "Jox-T", "http://i.imgur.com/3TLT7QN.png", "The idea of this tee is to have the hitter work on getting their bat head through the zone to make full contact with the ball while involving no recollection of the hit ball. ");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("AH2@sdc.com",1, "Automated Heliodon V2.0", "http://i.imgur.com/3TLT7QN.png", "Our goal is to modernize this technology with simple control systems and a user-friendly interface.");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("UEC@sdc.com",1, "Universally Expanding Cage", "http://i.imgur.com/3TLT7QN.png", "The Universally Expanding Cage (UEC) can expand in height just like the existing devices, but the UEC can simultaneously expand both laterally and posteriorly. ");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("WE@sdc.com",1, "Wastewater Evaporator", "http://i.imgur.com/3TLT7QN.png", "The Surface Evaporator is an economical approach to providing a low-cost evaporation solution to EPA regulated counties across the United States. ");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("APEL@sdc.com",1, "Accurate Photo-Emitting Liquid (APEL) Scan", "http://i.imgur.com/3TLT7QN.png", "We also take advantage of CMOS technology with a SiPM fabricated on a chip that requires much less power than traditional ones. ");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("CTRM@sdc.com",1, "Carpal Tunnel Relief Massager", "http://i.imgur.com/3TLT7QN.png", "Our team’s solution is to build a device that mimics the actions of a physical therapist so clients can have consecutive sessions without a doctor visit.");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("HHV3@sdc.com",1, "Hailey's Hang V3.0", "http://i.imgur.com/3TLT7QN.png", "Hailey’s Hand 3.0 addresses the identified problems, and either improves upon, or expands the scope of, previous Hailey’s Hand Designs. ");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("SPHERD@sdc.com",1, "Silicon Photomultiplier High Energy Radiation Detector", "http://i.imgur.com/3TLT7QN.png", "Our solution will utilize an in-house designed SiPM that will greatly reduce the cost of a scintillation counter device.");
INSERT INTO exhibitors(exhibitor_email, conference, company_name, logo_url, bio) VALUES("ZMED@sdc.com",1, "Z-Med Alert", "http://i.imgur.com/3TLT7QN.png", "Z-med Alert attempts to balance all the features of the current market products by using newer technology at a significantly reduced cost.");
